package com.betterman.algorithms.leetcode.hard;

/**
 * Created by zhuangda on 11/8/15.
 */
public class ShortestPalindrome {
    /**
     * Given a string S, you are allowed to convert it to a palindrome by adding characters in front of it.
     * Find and return the shortest palindrome you can find by performing this transformation.
     * For example:
     * <p>
     * Given "aacecaaa", return "aaacecaaa".
     * <p>
     * Given "abcd", return "dcbabcd".
     */
    public static class Solution {
        public String getShortestStringToMakePalindrome(String str) {
            if (str == null) return "";
            str = str.trim();

            if (str.isEmpty()) return "";

            String s = preprocess(str);

            int center = 0, range = 0;
            int p[] = new int[(s.length() - 1) / 2 + 1];
            int longestIndex = 0;
            for (int i = 1; i <= (s.length() - 1) / 2; i++) {
                if (range > i) {
                    int mirror = 2 * center - i;
                    if (i + p[mirror] < range) {
                        p[i] = p[mirror];
                        continue;
                    } else {
                        p[i] = range - i;
                    }
                }

                while (i - p[i] - 1 >= 0 && i + p[i] + 1 < s.length()) {
                    if (s.charAt(i - p[i] - 1) == s.charAt(i + p[i] + 1)) {
                        p[i]++;
                    } else {
                        break;
                    }
                }

                if (i + p[i] > range) {
                    center = i;
                    range = i + p[i];
                }

                if (p[i] == i) {
                    longestIndex = i;
                }
            }


            int l = (longestIndex - 1) / 2, r;
            if (longestIndex % 2 == 0) {
                r = l + 1;
            } else {
                r = l;
            }

            return new StringBuffer(str.substring(r + l + 1, str.length())).reverse().toString();
        }

        public String shortestPalindrome(String str) {
            if (str == null) return "";
            str = str.trim();

            if (str.isEmpty()) return "";

            return new StringBuffer(getShortestStringToMakePalindrome(str)).append(str).toString();
        }

        private String preprocess(String str) {
            StringBuffer sb = new StringBuffer();
            sb.append("#");
            for (int i = 0; i < str.length(); i++) {
                sb.append(str.charAt(i));
                sb.append("#");
            }
            return sb.toString();
        }
    }

    public static void main(String[] args) {
        String str =
//                ""
//                "abcd"
            "aba"
//                "aacecaaa"
//                "aabbaad"
//                "aa"
            ;

        Solution solution = new Solution();

        System.out.println(solution.shortestPalindrome(str));
    }
}
